import React, { Component, useState } from "react";
import { Form } from "reactstrap";


export class BetSlip extends Component 
{
    static displayName = BetSlip.name;

    render(){

        const [inputFields, setInputFields] = useState([
            {
                bet:'', 
                market:'',
                actualOdds:'',
                minOdds:''
            }
        ])
        return(<Form>

                {inputFields.map((input, index) => 
                    { return( <div key={index}>
                        <label>Bet
                            <input name="Bet" placeholder="Bet"></input>
                        </label>
                        <label>Market
                            <input name="Market" placeholder="Market"></input>
                        </label>
                        <label>Actual Odds
                            <input name="Actual Odds" placeholder="Actual Odds"></input>
                        </label>
                        <label>Minimum Odds
                            <input name="Minimum Odds"></input>
                        </label>
                    </div>)}
                )}

        </Form>)
    } 
}
