import React, { useState } from "react"

export default function CreateRow (){

    const[inputFields, setInputFields] = useState([
       {
           bet:'', 
           market:'',
           actualOdds:'',
           minOdds:''
       }
   ])

   const handleFormChanage =() => {

   }

   return(
    <Form>
       {inputFields.map((input, index) => 
           { 
               return( <div key={index}>
               <label>Bet
                   <input name="Bet" 
                   placeholder="Bet" 
                   value={input.bet}>
                   onChange={event => handleFormChange(index, event)}    
                   </input>
               </label>
               <label>Market
                   <input name="Market" 
                   placeholder="Market" 
                   value={input.market}>
                   onChange={event => handleFormChange(index, event)}
                   </input>
               </label>
               <label>Actual Odds
                   <input name="Actual Odds" 
                   placeholder="Actual Odds" 
                   value={input.actualOdds}>
                   onChange={event => handleFormChange(index, event)}
                   </input>
               </label>
               <label>Minimum Odds
                   <input name="Minimum Odds" 
                   placeholder="Minimum Odds" 
                   value={input.minOdds}>
                   onChange={event => handleFormChange(index, event)}
                   </input>
               </label>
            </div>)
            }
       )}
   </Form>)

}
