import { Form } from "reactstrap"
import React, { useState } from "react"

export default function CreateRow (){

    const[inputFields, setInputFields] = useState([
       {
           bet:'', 
           market:'',
           actualOdds:'',
           minOdds:''
       }
   ])

   return(
    <Form>
       {inputFields.map((input, index) => 
           { 
               return( <div key={index}>
               <label>Bet
                   <input name="Bet" placeholder="Bet" value={input.bet}></input>
               </label>
               <label>Market
                   <input name="Market" placeholder="Market" value={input.market}></input>
               </label>
               <label>Actual Odds
                   <input name="Actual Odds" placeholder="Actual Odds" value={input.actualOdds}></input>
               </label>
               <label>Minimum Odds
                   <input name="Minimum Odds" value={input.minOdds}></input>
               </label>
            </div>)
            }
       )}
   </Form>)

}
